<?phpnamespace App\Http\Controllers;use App\CategoryPost;use App\Employee;use App\Menu;use App\Post;use App\Slider;use Illuminate\Http\Request;class HomePageController extends Controller{    public function getInfoHomepage()    {        $sliders = Slider::where('slider_is_active', '=', '1')->orderBy('slider_order', 'DESC')->get();        return view('frontend.home.index', compact('emps', 'sliders'));    }    public function getContentGioiThieu()    {        $content = Post::where('path', '=', 'gioi-thieu')->first();        return view('frontend.gioithieu.index', compact('content'));    }    public function getDetailTuyenDung($path)    {        $tuyendung = Post::where('path', '=', $path)->first();        return view('frontend.tuyendung.detail.index', compact('tuyendung'));    }    public function getDetailTinTuc($path)    {        $tintuc = Post::where('path', '=', $path)->first();        $order_tintucs = Post::where('post_type', '=', 2)->where('id', '!=', $tintuc->id)->take(4)->get();        return view('frontend.tintuc.detail.index', compact('tintuc', 'order_tintucs'));    }    public function getMainPage($path)    {        $content = Post::where('path', $path)->first();        switch ($path) {            case 'lien-he':                return view('frontend.contact.contact1', compact('content'));            case'gioi-thieu':                return view('frontend.gioithieu.index', compact('content'));        }//        return view('frontend.' . $content->template, compact('content'));    }    public function getServicePage($path){        $content = Post::where('path', $path)->first();        switch ($path){            case 'google-adwords':                return view('frontend.dichvu.googleadwords.index', compact('content'));            case 'thiet-ke-website':                return view('frontend.dichvu.website.index', compact('content'));            case 'facebook-ads':                return view('frontend.dichvu.facebook.index', compact('content'));            default:                return view('frontend.dichvu.common.index', compact('content'));        }    }    public function getContentCategory($path)    {        $categoryPost = CategoryPost::where('path', $path)->first();        $posts = Post::where('post_type', '=', $categoryPost->id)->get();        $page = Post::find($categoryPost->page_id);        return view('frontend.' . $categoryPost->template, compact('page', 'posts'));    }    public function getAllTinTuc(){        $data = [];        $newsMain = CategoryPost::where('path', 'tin-tuc')->first();        $news = Post::where('post_type', $newsMain->id)->get();        $data['newsMain'] = $newsMain;        $data['news'] = $news;        return view('frontend.tintuc.index', compact('data'));    }    public function getContentHoiDap($path){        $data = [];        $postMain = CategoryPost::where('path', $path)->first();        $post=Post::where('post_type', $postMain->id)->get();        $data['postMain'] = $postMain;        $data['post'] = $post;        return view('frontend.hoidap.index', compact('data'));    }    public function getAllTuyenDung(){        $data = [];        $tuyendungMain = CategoryPost::where('path', 'tuyen-dung')->first();        $tuyendungs = Post::where('post_type', $tuyendungMain->id)->get();        $data['tuyendungMain'] = $tuyendungMain;        $data['tuyendungs'] = $tuyendungs;        return view('frontend.tuyendung.index', compact('data'));    }}