<?phpnamespace App\Http\Controllers;use App\CategoryPost;use App\Employee;use App\Menu;use App\Post;use App\Slider;use Illuminate\Http\Request;class HomePageController extends Controller{    public function getInfoHomepage()    {        $sliders = Slider::where('slider_is_active', '=', '1')->orderBy('slider_order', 'DESC')->get();        return view('frontend.home.index', compact('emps', 'sliders'));    }    public function getContentGioiThieu()    {        $content = Post::where('path', '=', 'gioi-thieu')->first();        return view('frontend.gioithieu.index', compact('content'));    }    public function getDetailTuyenDung($path)    {        $tuyendung = Post::where('path', '=', $path)->first();        return view('frontend.tuyendung.detail.index', compact('tuyendung'));    }    public function getDetailTinTuc($path)    {        $tintuc = Post::where('path', '=', $path)->first();        $order_tintucs = Post::where('post_type', '=', 2)->where('id', '!=', $tintuc->id)->take(4)->get();        return view('frontend.tintuc.detail.index', compact('tintuc', 'order_tintucs'));    }    public function getContentPage($path)    {        $content = Post::where('path', $path)->first();        return view('frontend.' . $content->template, compact('content'));    }    public function getContentCategory($path)    {        $categoryPost = CategoryPost::where('path', $path)->first();        $posts = Post::where('post_type', '=', $categoryPost->id)->get();        $page = Post::find($categoryPost->page_id);        return view('frontend.' . $categoryPost->template, compact('page', 'posts'));    }}