<?phpnamespace App\Http\Controllers;use App\Employee;use App\Post;use App\Slider;use Illuminate\Http\Request;class HomePageController extends Controller{    public function getInfoHomepage()    {        $emps = Employee::where('emp_is_work', '=', '1')->orderBy('emp_order', 'ASC')->get();        $sliders = Slider::where('slider_is_active', '=', '1')->orderBy('slider_order', 'DESC')->get();        return view('frontend.home.index', compact('emps', 'sliders'));    }    public function getContentGioiThieu()    {        $content = Post::where('path', '=', 'gioi-thieu')->first();        return view('frontend.gioithieu.index', compact('content'));    }    public function getContentTuyenDung()    {        $content = Post::where('path', '=', 'tuyen-dung')->first();        $tuyendungs = Post::where('post_type', '=', 3)->get();        return view('frontend.tuyendung.index', compact('content', 'tuyendungs'));    }    public function getDetailTuyenDung($path)    {        $tuyendung = Post::where('path', '=', $path)->first();        return view('frontend.tuyendung.detail.index', compact('tuyendung'));    }    public function getContentBaoGia()    {        $emps = Employee::where('emp_is_work', '=', '1')->orderBy('emp_order', 'ASC')->get();        return view('frontend.baogia.baogia', compact('emps'));    }    public function getContentDichVu($path)    {        switch ($path) {            case 'google-adwords':                return view('frontend.dichvu.googleadwords.index');                break;            case 'thiet-ke-website':                return view('frontend.dichvu.website.index');                break;            case 'facebook-ads':                return view('frontend.dichvu.facebook.index');                break;            default:                $content = Post::where('path', '=', $path)->first();                return view('frontend.dichvu.common.index', compact('content'));        }    }    public function getContentHoiDap($path)    {        switch ($path) {            case 'hoi-dap-thiet-ke-website':                $page = Post::where('path', '=', $path)->first();                $hoidaps = Post::where('post_type', '=', 6)->get();                return view('frontend.hoidap.index', compact('page', 'hoidaps'));                break;            case 'hoi-dap-google-adwords':                $page = Post::where('path', '=', $path)->first();                $hoidaps = Post::where('post_type', '=', 5)->get();                return view('frontend.hoidap.index', compact('page', 'hoidaps'));                break;        }    }    public function getContentTinTuc()    {        $page = Post::where('path', '=', 'tin-tuc')->first();        $tintucs = Post::where('post_type', '=', 2)->get();        return view('frontend.tintuc.index', compact('page', 'tintucs'));    }    public function getDetailTinTuc($path)    {        $tintuc = Post::where('path', '=', $path)->first();        $order_tintucs = Post::where('post_type', '=', 2)->where('id', '!=', $tintuc->id)->take(4)->get();        return view('frontend.tintuc.detail.index', compact('tintuc', 'order_tintucs'));    }}